import React, { useState, useEffect } from "react";
import { fetchBlockDetails } from "../services/block";
import { Block } from "../types/block";

interface Props {
  blockNumber: string;
}

const BlockDetails: React.FC<Props> = ({ blockNumber }) => {
  const [block, setBlock] = useState<Block | null>(null);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadBlock = async () => {
      try {
        const blockData = await fetchBlockDetails(blockNumber);
        setBlock(blockData);
        setError(null);
      } catch (err) {
        setError("Failed to fetch block details");
        console.error(err);
      }
    };
    loadBlock();
  }, [blockNumber]);

  if (error) return <div className="result error">{error}</div>;
  if (!block) return <div className="result">Loading...</div>;

  return (
    <div className="result">
      <p><strong>Block #:</strong> {block.number}</p>
      <p>
        <strong>Time:</strong>{" "}
        {new Date(block.timestamp * 1000).toLocaleString()}
      </p>
      <p><strong>Transactions:</strong> {block.transactionCount}</p>
      <p><strong>Validator:</strong> {block.miner}</p>
    </div>
  );
};

export default BlockDetails;
