import React, { useState } from "react";
import TransactionDetails from "./TransactionDetails";
import BlockDetails from "./BlockDetails";
import "../assets/styles.css";

const Explorer: React.FC = () => {
  const [searchInput, setSearchInput] = useState("");
  const [searchType, setSearchType] = useState<"block" | "tx" | null>(null);

  const handleSearch = () => {
    // 입력값이 숫자면 Block Number로 간주, 그렇지 않으면 TX Hash로 간주
    if (/^\d+$/.test(searchInput)) {
      setSearchType("block");
    } else if (searchInput.startsWith("0x") && searchInput.length === 66) {
      setSearchType("tx");
    } else {
      setSearchType(null);
      alert("Please enter a valid Block Number or TX Hash");
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === "Enter") {
      handleSearch();
    }
  };

  return (
    <div className="explorer-container">
      <h1>Blockchain Explorer</h1>

      <div className="section">
        <input
          type="text"
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Enter Block Number or TX Hash"
          style={{ width: "80%", marginRight: "10px" }}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {searchType === "block" && searchInput && (
        <BlockDetails blockNumber={searchInput} />
      )}
      {searchType === "tx" && searchInput && (
        <TransactionDetails txHash={searchInput} />
      )}
    </div>
  );
};

export default Explorer;
